mapper.select:
select *
from file_info
where file_id = #{fileId}

select *
from hos_basic
where hos_code = #{hosCode}

select *
from member
where member_no = #{no}

select facility_name
from hos_facilitylist
where facility_code = #{facilityCode}

select * from hos_basic where (
title like CONCAT('%',#{keyWord},'%')
roadAddress like CONCAT('%',#{keyWord},'%') or address like CONCAT('%',#{keyWord},'%')
) and hos_register = 'Y' limit #{begin}, #{pageNum}

select * from hos_basic where (
title like CONCAT('%',#{keyWord},'%')
roadAddress like CONCAT('%',#{keyWord},'%') or address like CONCAT('%',#{keyWord},'%')
) and hos_register = 'N' limit #{begin}, #{pageNum}

select *
from hos_facility
where hos_code = #{hosCode}

mapper.update:
update hos_basic
set hos_register = 'Y',
	hos_comment = #{hosComment}
where hos_code = #{hosCode}

mapper.select:
select max(block_no)
from hos_block
where hos_code = #{hosCode}

select *
from hos_block
where block_day = #{blockDay}

	and hos_code = #{hosCode}

select *
from hos_block
where block_code = #{blockCode}

mapper.update:
update hos_block
set max_booking = #{maxBooking}
where block_code = #{blockCode}

mapper.select:
select *
from pet
where member_no = #{no}

select *
from hos_booking
where booking_no = #{bookingNo}

select count(*)
from hos_booking
where block_code = #{blockCode}

select *
from pet
where pet_no = #{petNo}

mapper.update:
update hos_booking
set confirm = 'Y'
where booking_no = #{bookingNo}

update hos_booking
set confirm = 'B'
where booking_no = #{bookingNo}

update hos_booking
set finish = 'Y'
where booking_no = #{bookingNo}

mapper.select:
select sum(max_booking)
from hos_block
where hos_code = #{hosCode}
	 and block_day = #{blockDay}
	 and facility_no = 1

select block_code
from hos_block
where hos_code = #{hosCode}
	 and block_day = #{blockDay}
	 and facility_no = 1

select sum(max_booking)
from hos_block
where hos_code = #{hosCode}
	 and block_day = #{blockDay}
	 and facility_no = 2

select block_code
from hos_block
where hos_code = #{hosCode}
	 and block_day = #{blockDay}
	 and facility_no = 2

select *
from hos_chart
where pet_no = #{petNo}

select max(hos_board_no)
from hos_board
where hos_code = #{hosCode}

mapper.update:
update file_info
set board_no = #{boardNo}
where file_id = #{fileId}

mapper.select:
select *
from hos_board
where hos_board_delete = 'N' 
and hos_code = #{hosCode}

and (

		hos_board_title like CONCAT('%',#{keyWord},'%')


		hos_board_category_code like #{keyWord}


		hos_board_writer_no in 
			(select member_no
			 from member
			 where member_nickName like CONCAT('%',#{keyWord},'%')
			) searchMember

)

limit #{begin}, #{pageNum}

select count(*)
from hos_board
where hos_board_delete = 'N' 
and hos_code = #{hosCode}

and (

		hos_board_title like CONCAT('%',#{keyWord},'%')


		hos_board_category_code like #{keyWord}


		hos_board_writer_no in 
			(select member_no
			 from member
			 where member_nickName like CONCAT('%',#{keyWord},'%')
			) searchMember

)

select *
from hos_board
where hos_board_id = #{hosBoardId}

mapper.update:
update hos_board
set hos_board_category_code = #{hosBoardCategoryCode},
	hos_board_title = #{hosBoardTitle},
	hos_board_reg_date = now(),
	hos_board_content = #{hosBoardContent}
where hos_board_id = #{hosBoardId}

mapper.select:
select count(*)
from hos_staff
where hos_code = #{hosCode}
	and staff_member_no = #{staffMemberNo}

mapper.update:
update hos_staff
set staff_title = #{staffTitle},
	hos_admin = #{hosAdmin}
where staff_no = #{staffNo}

update member
set hos_code = null
where member_no = #{memberNo}

update member
set hos_code = #{hosCode}
where member_no = #{staffMemberNo}

mapper.select:
select fav_hos_no
from fav_hos
where member_no = #{memberNo}
	and hos_code = #{hosCode}

select count(*)
from fav_hos
where hos_code = #{hosCode}

select *
from fav_hos
where member_no = #{memberNo}
