mapper.insert:
insert into member(
	member_email, password, member_grade, member_name, member_phone, member_nickname
) values (
	#{memberEmail}, #{password}, #{memberGrade}, #{memberName}, #{memberPhone}, #{memberNickname}
)

mapper.select:
select count(*)
  from member
 where member_email = #{memberEmail}

select *
  from member
 where member_email = #{memberEmail}
   and password = #{password}

mapper.insert:
insert into vet_auth(
	vet_ori_name, vet_sys_name, vet_file_path, vet_file_size, member_no
)values(
	#{vetOriName}, #{vetSysName}, #{vetFilePath}, #{vetFileSize}, #{memberNo}
)

mapper.select:
select *
  from member
 where member_name = #{memberName}
   and member_phone = #{memberPhone}

mapper.update:
update member
   set password = #{password}
 where member_no = #{memberNo}

update member
   set member_career = #{memberCareer}
 where member_no = #{memberNo}

update member
   set hos_code = #{hosCode}
 where member_no = #{memberNo}

update member set member_nickname = #{memberNickname},
member_file_size = #{memberFileSize}, member_file_path = #{memberFilePath}, member_ori_name = #{memberOriName}, member_sys_name = #{memberSysName},
member_phone = #{memberPhone} where member_no = #{memberNo}
