mapper.insert:
insert into tip(
board_code, category_code, member_no, title, content
	)values(
#{boardCode},#{categoryCode}, #{memberNo}, #{title}, #{content}
)

mapper.update:
update tip
   set title=#{title},
       content=#{content},
       category_code=#{categoryCode}
 where tip_no=#{tipNo}

update tip
   set view_cnt = view_cnt+1
 where tip_no = #{tipNo}

mapper.insert:
insert into tip_comment(
	tip_no, commenter_no, comment 
) values(
	#{tipNo}, #{commenterNo}, #{comment}
)

mapper.delete:
delete 
  from tip_comment
 where commenter_no = #{commenterNo}
   and comment_no = #{commentNo}

mapper.update:
update tip_comment 
   set comment = #{comment}  
 where commenter_no = #{commenterNo}
   and comment_no = #{commentNo}

mapper.select:
select count(*)
  from tip_comment
 where tip_no=#{tipNo}

select recommend
  from tip_recommend
 where member_no = #{memberNo}
   and tip_no=#{tipNo}

mapper.insert:
insert into tip_recommend(
	tip_no, member_no, recommend
)values(
	#{tipNo}, #{memberNo}, #{recommend}
)

mapper.delete:
delete 
  from tip_recommend
 where tip_no = #{tipNo}
   and member_no = #{memberNo}

mapper.select:
select recommend
  from tip_comment_recommend
 where comment_no = #{commentNo}
   and member_no = #{memberNo}

mapper.insert:
insert into tip_comment_recommend(
	comment_no, member_no, recommend
)values(
	#{commentNo}, #{memberNo}, #{recommend}
)

mapper.delete:
delete 
  from tip_comment_recommend
 where comment_no = #{commentNo}
   and member_no = #{memberNo}

mapper.insert:
insert into file_info(
	board_code, board_no, ori_name, sys_name, file_path, file_size
)values(
	#{boardCode}, #{boardNo}, #{oriName}, #{sysName}, #{filePath}, #{fileSize}
)
